    public void ItemCost() {
        double sum = 0;
        
        for (int i = 0; i < jTable1.getRowCount(); i++) {
            sum = sum + Double.parseDouble(jTable1.getValueAt(i, 2).toString());
        }
        
        jTextSubtotal.setText(Double.toString(sum));
        double cTextTotal = Double.parseDouble(jTextSubtotal.getText());
        
        double cTextDiscount = (cTextTotal * (3.60 / 100));
        String iTextTotal = String.format("RM %.2f", cTextDiscount);
        jTextDiscount.setText(iTextTotal);
                
        String iTextSubtotal = String.format("RM %.2f", cTextTotal);
        jTextSubtotal.setText(iTextSubtotal);
        
        String iTotal = String.format("RM %.2f", cTextTotal - cTextDiscount);
        jTextTotal.setText(iTotal);
    }
    
    public void Change() {
        double sum = 0;
        double cash = Double.parseDouble(jTextDisplay.getText());
        
        for (int i = 0; i < jTable1.getRowCount(); i++) {
            sum = sum + Double.parseDouble(jTable1.getValueAt(i, 2).toString());
        }
        
        double cTextDiscount = (sum / 3.60)/100;
        double cChange = (cash - (sum + cTextDiscount));
        
        String ChangeGiven = String.format("RM %.2f", cChange);
        jTextChange.setText(ChangeGiven);
    }
