    private void jButtonPrintActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonPrintActionPerformed
        MessageFormat header = new MessageFormat("Printing in progress");
        MessageFormat footer = new MessageFormat("Page {0, number, interger}");
        
        try {
            jTable1.print(JTable.PrintMode.NORMAL, header, footer);
        
        } catch (PrinterException ex) {
            System.err.format("No printer found!", ex.getMessage());
            //Logger.getLogger(CashierApp.class.getName()).log(Level.SEVERE, null, ex);
        }
       
    }//GEN-LAST:event_jButtonPrintActionPerformed

    private void jButtonRemoveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonRemoveActionPerformed
        DefaultTableModel model = (DefaultTableModel) jTable1.getModel();
        
        int RemoveItem = jTable1.getSelectedRow();
        
        if (RemoveItem >= 0) {
            model.removeRow(RemoveItem);
        }
        ItemCost();
        
        if (jComboPayment.getSelectedItem().equals("Cash")) {
            Change();
        } 
        
        else {
            jTextChange.setText("");
            jTextDisplay.setText("");
        }
    }//GEN-LAST:event_jButtonRemoveActionPerformed

